const buttons=Array.from(document.querySelectorAll(".service__button--more")),descriptions=Array.from(document.querySelectorAll(".service__description"));for(let t=0;t<buttons.length;t++)buttons[t].addEventListener("click",(function(s){buttons[t].classList.contains("closed")?descriptions.map(s=>buttons[t].id===s.id&&(s.classList.add("description_show"),buttons[t].textContent="Подробнее",buttons[t].classList.remove("closed"))):descriptions.map(s=>buttons[t].id===s.id&&(s.classList.remove("description_show"),buttons[t].textContent="Скрыть",buttons[t].classList.add("closed")))}));